"use client";

import { useRef } from "react";
import emailjs from "@emailjs/browser";
import { Link } from "react-router-dom";
import "./queryform.css"; // ðŸ‘ˆ we'll move the CSS here

export default function ContactForm() {
    const formRef = useRef();

    const sendEmail = (e) => {
        e.preventDefault();

        emailjs
            .sendForm(
                "service_nh86wvl",   // âœ… Replace with your Service ID
                "template_19cpuat", // âœ… Replace with your Template ID
                formRef.current,
                { publicKey: "PTD1pglSvedAnAHyf" } // âœ… Replace with your Public Key
            )
            .then(
                (response) => {
                    alert("Message sent âœ…");
                    console.log("SUCCESS", response);
                },
                (error) => {
                    alert("Failed âŒ");
                    console.error("FAILED", error);
                }
            );
    };
    const goHome = () => {
        window.location.href = "/";
    };
    return (
        <div>
            <button
                className="back-btn"
                onClick={goHome}
                aria-label="Go back"
                style={{
                    cursor: 'pointer !important',
                    background: 'red',
                    borderRadius: '50%',
                    border: '5px solid red',
                    fontSize: '40px',
                    fontWeight: 'bold',
                    width: '34px',
                    height: '34px',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    userSelect: 'none',
                    padding: 0,
                    lineHeight: 1,
                    color: 'black',
                    zIndex: 1000
                }}
            >
                &#8592;
            </button>
            <div className="login-box">
                <h2>Contact Us</h2>
                <form ref={formRef} onSubmit={sendEmail}>
                    <div className="user-box">
                        <input type="text" name="name" required />
                        <label>Name</label>
                    </div>

                    <div className="user-box">
                        <input type="email" name="email" required />
                        <label>Email</label>
                    </div>

                    <div className="user-box">
                        <textarea name="message" required />
                        <label>Message</label>
                    </div>

                    <button type="submit">Send</button>
                </form>
            </div>
        </div>
    );
}
